import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';


const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = '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';

function ChatPage({ location }) {
  const [loading, setLoading] = useState(false);
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);

  const { state = {} } = location || {};
  const { symptoms, duration, patterns, medications } = state;


  const sendBotMessage = (content) => {
    const botMessage = { role: 'assistant', content };
    setMessages((prevMessages) => [...prevMessages, botMessage]);
  };

  const sendMessage = async () => {
    if (!userInput.trim()) return;
    setLoading(true);

    const userMessage = { role: 'user', content: userInput };
    // If needed, modify the systemMessage content as per your needs.
    const systemMessage = {
      role: 'assistant',
      content: `Symptoms: ${symptoms}. Duration of symptoms: ${duration}. Patterns of symptoms: ${patterns}. Symptoms: ${medications}`,
    };

    try {
      const response = await axios.post(API_URL, {
        model: 'gpt-3.5-turbo',
        messages: [...messages, systemMessage, userMessage],
      }, {
        headers: {
          'Authorization': `Bearer ${API_KEY}`,
          'Content-Type': 'application/json',
        },
      });

      const botMessage = { role: 'system', content: response.data.choices[0].message.content.trim() };
      setMessages((prevMessages) => [...prevMessages, userMessage, botMessage]);
    } catch (error) {
      console.error("Error response from OpenAI:", error.response?.data);
    }

    setUserInput('');
    setLoading(false);
  };



  return (
    <div className="chat-container">
      <div className="chat-box">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.role}`}>
            <span>{message.content}</span>
          </div>
        ))}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type a message..."
          disabled={loading}
        />
        <button onClick={sendMessage} disabled={loading || !userInput.trim()}>
          {loading ? 'Loading...' : 'Send'}
        </button>
      </div>
    </div>
  );
}

export default ChatPage;
